import React, { useState, useEffect } from "react";
import { StyledLink } from "./StyledLink"; // Make sure to import your StyledLink and NavItem components
import { NavItem } from "./NavItem";
import { getCurrentUser } from "./auth"; // Import the function to get the current user
import { Container } from "./Container"; // Assuming you have a Container component

export function Navbar() {
  const [current, setCurrent] = useState(false);

  useEffect(() => {
    const currentCheck = async () => {
      try {
        const loginCurrent = await getCurrentUser();
        setCurrent(!!loginCurrent);
      } catch (error) {
        console.log(error);
        setCurrent(false);
      }
    };
    currentCheck();
  }, []);

  return (
    <>
      <Container>
        <StyledLink to="/home">
          <NavItem icon="ti ti-home" name="홈" />
        </StyledLink>
        <StyledLink to="/lecture">
          <NavItem icon="ti ti-device-tv" name="강의" />
        </StyledLink>
        <StyledLink to="/cart">
          <NavItem icon="ti ti-shopping-cart" name="장바구니" />
        </StyledLink>
        <StyledLink to="/mypage/user">
          <NavItem icon="ti ti-user" name="마이페이지" />
        </StyledLink>
        <StyledLink to="/community">
          <NavItem icon="ti ti-friends" name="커뮤니티" />
        </StyledLink>

        {current ? (
          <StyledLink to="/logout">
            <NavItem icon="ti ti-logout" name="로그아웃" />
          </StyledLink>
        ) : (
          <StyledLink to="/login">
            <NavItem icon="ti ti-login" name="로그인" />
          </StyledLink>
        )}

        {current && (
          <StyledLink to="/admin/user">
            <NavItem icon="ti ti-settings" name="관리자" />
          </StyledLink>
        )}
      </Container>
    </>
  );
}
