import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getLecture } from "../../../Api/LectureApi/LectureApi";
import styled from "styled-components";
import {
  getContentsByLectureId,
  getReviewByLectureId,
} from "../../../Api/LeaningApi/LeaningApi";
import { getTeacherByLectureId } from "../../../Api/TeacherApi/TeacherApi";

const Container = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: 20% 80%;
`;

const SideMenu = styled.div`
  width: 100%;
  border: 1px solid black;
`;

const Test = styled.div`
  width: 100%;
  border: 1px solid yellow;
`;

const LectureImg = styled.img`
  width: 50%;
`;

export function LectureDetail() {
  const { lectureId } = useParams(); // URL에서 lectureId를 가져옴
  const [lecture, setLecture] = useState([]); // lecture 데이터를 저장할 상태
  const [content, setContent] = useState([]);
  const [teacher, setTeacher] = useState([]);
  const [review, setReview] = useState([]);

  // Lecture 데이터
  useEffect(() => {
    async function LectureAxios() {
      try {
        const LectureData = await getLecture(lectureId); // getLecture API 호출
        setLecture(LectureData); // 가져온 데이터를 상태로 저장
      } catch (error) {
        console.log("Lecture Error", error); // 오류가 발생하면 오류 상태로 설정
      }
    }
    LectureAxios(); // 컴포넌트가 마운트될 때 API 호출
  }, [lectureId]); // lectureId가 변경될 때마다 다시 호출

  // Content 데이터
  useEffect(() => {
    async function ContentAxios() {
      try {
        const ContentData = await getContentsByLectureId(lectureId);
        setContent(ContentData);
      } catch (error) {
        console.log("Content Error", error);
      }
    }
    ContentAxios();
  }, [lectureId]);

  // Teacher 데이터
  useEffect(() => {
    async function TeacherAxios() {
      try {
        const TeacherData = await getTeacherByLectureId(lectureId);
        setTeacher(TeacherData);
      } catch (error) {
        console.log("Teacher Error", error);
      }
    }
    TeacherAxios();
  }, [lectureId]);

  // Review 데이터
  useEffect(() => {
    async function ReviewAxios() {
      try {
        const ReviewData = await getReviewByLectureId(lectureId);
        setReview(ReviewData);
      } catch (error) {
        console.log("Review Error", error);
      }
    }
    ReviewAxios();
  }, [lectureId]);

  if (!lecture && !content && !teacher) {
    return <p>No data found.</p>; // 데이터가 없을 때 표시할 내용
  }

  console.log(review);
  return (
    <Container>
      <SideMenu></SideMenu>
      <Test>
        <LectureImg src={lecture.imagePath}></LectureImg>
        <p>*Lecture Data*</p>
        <p>Lecture ID: {lectureId}</p>
        <p>Lecture Title: {lecture.lectureName}</p>
        <p>Lecture Description: {lecture.learningObject}</p>
        <p>　</p>
        <p>*Content Data*</p>
        {content.map((data, index) => (
          <p key={index}>
            {index}.{data.learningContents}
          </p>
        ))}
        <p>　</p>
        <p>*Teacher Data*</p>
        {teacher.map((data, index) => (
          <p key={index}>{data.teacherResume}</p>
        ))}
        <p>　</p>
      </Test>
    </Container>
  );
}
