import React, { useState, useEffect, useRef } from "react";
import { Unity, useUnityContext } from "react-unity-webgl";
import styled from "styled-components";

import Modal from "react-modal";
import { ModalNotices } from "../LMS/Community/ModalNotices.js";

import { Lecture } from "../LMS/Lecture/Lecture.js";
import { LectureList } from "../LMS/Lecture/LectureList.js";

import { UserManagement } from "../LMS/Admin/UserManagement.js";

import axios from "axios";

// 게임을 로드할 화면을 만듬
const Container = styled.div`
  width: 1344px;
  height: 756px;
  margin: auto;
  border: 1px solid gray;
`;

const customStyles = {
  content: {
    top: "10%",
    left: "10%",
    right: "auto",
    bottom: "auto",
    transform: "translate(-50%, -50%)",
    width: "100px", // 모달의 너비를 설정합니다.
    height: "80px", // 모달의 높이를 설정합니다.
    padding: "20px",
    borderRadius: "10px",
  },
  overlay: {
    backgroundColor: "rgba(0, 0, 0, 0.5)", // 오버레이의 배경색을 설정합니다.
  },
};

export function UnityProject() {
  const [playingGame, setPlayingGame] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);
  const [userName, setUserName] = useState();
  const [score, setScore] = useState();

  const [modalOpen, setModalOpen] = useState(false);
  const modalBackground = useRef();

  const [modalReturn, setModalReturn] = useState(null);
  const [massage, setMassage] = useState("");

  // React 에서 Unity 로 sendMessage 를 통해 전달하기
  const { unityProvider, sendMessage, addEventListener, removeEventListener } =
    useUnityContext({
      loaderUrl: "build/Build.loader.js",
      dataUrl: "build/Build.data",
      frameworkUrl: "build/Build.framework.js",
      codeUrl: "build/Build.wasm",
    });

  // Unity 에서 React 로 전달
  function handleGameOver(userName, score) {
    setIsGameOver(true);
    setUserName(userName);
    setScore(score);
  }

  useEffect(() => {
    handleOpenReactWindowNotices();
  }, []);

  // Unity에서 호출될 JavaScript 함수
  function handleOpenReactWindowNotices(romeName) {
    if (romeName === "공지사항") {
      window.alert("Modal Open 공지사항!!! 전달됨");
      setMassage("PauseGame");
      sendMessage("Player", massage);
      setModalOpen(true);
      setModalReturn(() => LectureList); // React component function
    }
  }

  useEffect(() => {
    addEventListener("OpenReactWindowNotices", handleOpenReactWindowNotices);
    return () => {
      removeEventListener(
        "OpenReactWindowNotices",
        handleOpenReactWindowNotices
      );
    };
  }, [addEventListener, removeEventListener]);

  useEffect(() => {
    addEventListener("GameOver", handleGameOver);
    return () => {
      removeEventListener("GameOver", handleGameOver);
    };
  }, []);

  function ReactToUnityJSON() {
    const urlLectureContentQA = `http://localhost:8080/learning/contents/qa/L00000000052/3`; // /{lectureId}/{learningContentsSeq}

    window.alert("urlLectureContentQA: " + urlLectureContentQA);

    axios
      .get(urlLectureContentQA, {
        withCredentials: true,
      })
      .then((response) => {
        console.log("데이터:", response.data);
        window.alert(
          "response.data: " +
            response.data[0].question +
            "@" +
            response.data[1].question +
            "@" +
            response.data[2].question
        );

        const jsonData = JSON.stringify(response.data);

        window.alert("React jsonData: " + jsonData);

        window.alert("SendMessage Start!!!");

        sendMessage("Player", "ReceiveJsonData", jsonData);
      })
      .catch((error) => {
        console.log("에러 발생: ", error);
      });
  }

  // 모달을 닫는 함수
  function closeModal() {
    setModalOpen(false);
    setMassage(null); // setMassage를 null로 설정
    setPlayingGame(true); // 게임을 다시 시작
    sendMessage("Player", "ContinueGame"); // Unity로 ContinueGame 메시지 보내기
  }

  return (
    <>
      <h1>UnityProject Game</h1>
      <button onClick={() => setPlayingGame(true)}>Start Game</button>
      <button onClick={() => sendMessage("Player", "PauseGame")}>
        Pause Game
      </button>
      <button onClick={() => sendMessage("Player", "ContinueGame")}>
        Continue Game
      </button>
      <button onClick={() => sendMessage("Player", "Attack")}>Attack</button>
      <button onClick={() => ReactToUnityJSON()}>ReactToUnityJSON</button>
      <Container>
        {modalOpen && (
          <div
            className={"modal-container"}
            ref={modalBackground}
            onClick={(e) => {
              if (e.target === modalBackground.current) {
                closeModal();
              }
            }}
          >
            <Modal
              isOpen={modalOpen}
              onRequestClose={closeModal}
              style={customStyles}
              contentLabel="Modal Test"
            >
              <span className="close" onClick={closeModal}>
                &times;
              </span>
              <h1>리액트로 모달</h1>
              {modalReturn && React.createElement(modalReturn)}
              <br />
              <button onClick={closeModal}>모달 닫기</button>
            </Modal>
          </div>
        )}
        {playingGame ? (
          <Unity
            unityProvider={unityProvider}
            style={{
              width: "100%",
              height: "100%",
            }}
          />
        ) : null}
      </Container>

      {isGameOver === true && (
        <p>{`Game Over ${userName}! You've scored ${score} points.`}</p>
      )}
    </>
  );
}
