import { useState, useEffect, useContext } from "react";
import {
  getGenre,
  getAllLectures,
  getRecommendedLectures,
  getFreeLectures,
  getPaidLectures,
  getNewLectures,
  setGenreListOfMovie,
  IMG_PATH,
} from "../api";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import { LMSContext, LMSWrapper } from "../LMSWrapper";
import { getCategory } from "../../../Api/CategoryApi/CategoryApi";
import { getAllLecture } from "../../../Api/LectureApi/LectureApi";

const Container = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 10px;
`;

const Card = styled.div`
  width: 100%;
  border: 1px solid dodgerblue;
  cursor: pointer;
`;

const Img = styled.img`
  width: 100%;
`;

const Text = styled.div``;

const Tab = styled.div`
  display: flex;
  margin: 10px 0;
`;

const Button = styled.button`
  width: 150px;
  height: 40px;
  //background-color: dodgerblue;
  background-color: ${(props) =>
    props.className === "selected" ? "green" : "dodgerblue"};
  border: none;
  color: white;
  padding: 5px 15px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 1.1rem;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 4px;
  transition: background-color 0.3s ease;
  // 마위스 위에
  &:hover {
    background-color: blue;
  }
  // 마우스 클릭시
  &:active {
    background-color: darkblue;
  }
  // 클래스 active 이면 this.active
  &.active {
    background-color: green;
  }
`;

export function LectureList() {
  const [data, setData] = useState(null);
  const [LectureData, setLectureData] = useState([]);
  // const [category, setCategory] = useState(0); => 부모, 즉 LMSWrapper 로 이동시킴

  const { category, setCategory } = useContext(LMSContext); // 부모, 즉 LMSWrapper 에서 생성한 LMSContext 에서 꺼내서 사용

  // useNavigage 후크는 url 주소를 매개변수로 갖는 페이지 변경 함수를 리턴함!
  const navigate = useNavigate();

  useEffect(() => {
    setGenreListOfMovie();
    getMovies(category);
  }, []);

  // useEffect(() => {
  //     getMovies("N");
  //     setSelectedButton("N");
  // }, []);

  async function getMovies(index) {
    try {
      setCategory(index);
      const response = await categories[index].func();
      console.log(response.data);
      setData(response.data);
    } catch (error) {
      console.log("Error", error);
    }
  }

  useEffect(() => {
    const fetchData = async () => {
      const LectureData = await getAllLecture(); // getCategory 함수 호출
      console.log("데이터:", LectureData); // 데이터를 콘솔에 출력
    };

    fetchData(); // fetchData 함수 호출
  }, []); // 빈 배열을 두 번째 인수로 전달하여 컴포넌트 마운트 시 한 번만 실행되도록 함

  // async function getMovies(type) {
  //     try {
  //         let response;
  //         switch (type) {
  //             case "N":
  //                 response = await getMoviesNowPlaying();
  //                 break;
  //             case "P":
  //                 response = await getMoviesPopular();
  //                 break;
  //             case "T":
  //                 response = await getMoviesTopRated();
  //                 break;
  //             case "U":
  //                 response = await getMoviesUpcoming();
  //                 break;
  //             default:
  //                 response = await getMoviesNowPlaying();
  //                 break;
  //         }

  //         console.log(response.data);

  //         setData(response.data);
  //     } catch (error) {
  //         console.log("Error", error);
  //     }
  // }

  const categories = [
    { category: "전체", func: getAllLectures },
    { category: "추천", func: getRecommendedLectures },
    { category: "무료", func: getFreeLectures },
    { category: "유료", func: getPaidLectures },
    { category: "신규", func: getNewLectures },
  ];

  return (
    <>
      <h1>강의 리스트</h1>
      <Tab>
        {
          // {} 는 return 들어가야 되고, 아니면 ()
          categories.map((c, i) => (
            <Button
              key={i}
              onClick={() => getMovies(i)}
              className={i == category ? "active" : ""}
            >
              {c.category}
            </Button>
          ))
        }
      </Tab>
      <Container>
        {LectureData &&
          LectureData.map((movie) => (
            <Card
              key={movie.lectureId}
              onClick={() => {
                navigate(`${movie.id}`);
              }}
            >
              {/* <Img src={IMG_PATH + movie.poster_path}></Img>
              <Text>타이틀 : {movie.title}</Text>
              <Text>장르 : {getGenre(movie.genre_ids)}</Text>
              <Text>소개글 : {movie.overview}</Text> */}
            </Card>
          ))}
      </Container>
    </>
  );
}
