import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
} from "../../../Api/CommunityApi/CommunityApi";

const BoardContainer = styled.div`
  width: 100%;
  height: 200px;
  display: grid;
  grid-template-columns: 50% 50%;
  padding: 10px;
`;

const Notices = styled.div`
  width: 100%;
  height: 50px;
  border: 1px solid orange;
`;
const Event = styled.div`
  width: 100%;
  height: 50px;
  border: 1px solid blue;
`;

export function IndexCommunity() {
  const [notices, setNoteces] = useState([]);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios();
  }, []);

  async function axios() {
    try {
      const noticesData = await getAllNotices();
      setNoteces(noticesData);

      const eventsData = await getAllLmsEvents();
      const eventsArray = eventsData.events || []; // 이벤트 배열을 얻거나 빈 배열로 설정
      const limitedEvents = eventsArray.slice(0, 5); // 첫 5개 요소만 추출
      //   setEvents(eventsData);
    } catch (error) {
      console.log("Error", error);
    }
  }

  console.log("1", notices);
  console.log("2", events);
  return (
    <BoardContainer>
      <Notices>
        <p></p>
      </Notices>
      <Event></Event>
    </BoardContainer>
  );
}
