import { useParams } from "react-router-dom";
import { getContentsByLectureId } from "../../../Api/LeaningApi/LeaningApi";
import { useEffect, useState } from "react";
import { getTeacherByLectureId } from "../../../Api/TeacherApi/TeacherApi";

export function Course() {
  const { userId, lectureId } = useParams();
  const [learning, setlearing] = useState([]);
  const [lecture, setLecture] = useState([]);
  const [teacher, setTeacher] = useState([]);

  useEffect(() => {
    learningContents();
    teacherData();
  }, [lectureId]);

  // learning Content 데이터
  async function learningContents() {
    try {
      const learningAxiox = await getContentsByLectureId(lectureId);
      console.log(learningAxiox[0]);
      setlearing(learningAxiox);
    } catch (error) {
      console.log("leaning Error", error);
    }
  }
  // lecture 데이터
  const lectureData = learning[0];

  // Teacher 데이터
  async function TeacherAxios() {
    try {
      const TeacherData = await getTeacherByLectureId(lectureId);
      setTeacher(TeacherData);
    } catch (error) {
      console.log("Teacher Error", error);
    }
  }

  return (
    <>
      <p>　</p>
      <p>*학습목차*</p>
      {learning.map((data, index) => (
        <p key={index}>
          {index}.{data.learningContents}
        </p>
      ))}
      <p>　</p>
      <p>*Lecture Data*</p>
      <p>{lectureData.lecture.lectureId}</p>
      <p>{lectureData.lecture.lectureName}</p>
      <p>　</p>
      <p>*Teacher Data*</p>
      {/* <p>{teacher.teacherName}</p> */}
    </>
  );
}
