import React, { useState } from "react";
import axios from "axios";

const SignUp = ({ onSuccess }) => {
  const [signupUserId, setSignupUserId] = useState("");
  const [signupPassword, setSignupPassword] = useState("");
  const [signupUserName, setSignupUserName] = useState("");
  const [signupEmail, setSignupEmail] = useState("");

  const handleSignup = async () => {
    if (signupUserId && signupPassword && signupUserName && signupEmail) {
      try {
        const data = {
          userId: signupUserId,
          password: signupPassword,
          userName: signupUserName,
          userEmail: signupEmail,
        };
        const response = await axios.post(
          "http://localhost:8080/user/signup",
          data,
          { withCredentials: true }
        );
        if (response.status === 201) {
          alert("회원가입이 완료되었습니다.");
          onSuccess();
        }
      } catch (error) {
        console.log("회원가입 에러 발생: ", error);
      }
    } else {
      alert("가입정보를 모두 입력하여 주세요!!!");
    }
  };

  return (
    <LoginContainer>
      <Loginbg></Loginbg>
      <LoginSide>
        {loginCurrent ? (
          <LoginBox>
            <LoginForm>
              <H1>{userId}님 어서오세요</H1>
              <NavigateBtn bgColor="#b4a6a6"> 강의실 가기</NavigateBtn>
              <NavigateBtn
                bgColor="#c1d3fc"
                onClick={() => {
                  setLoginCurrent(false);
                }}
              >
                홈페이지
              </NavigateBtn>
            </LoginForm>
          </LoginBox>
        ) : (
          <LoginBox>
            <LoginForm>
              <H1>로그인</H1>
              <InputBox
                type="text"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                placeholder="아이디"
              />
              <InputBox
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="비밀번호"
              />
              <SummitBtn onClick={handleLogin}>로그인</SummitBtn>
              <SginUpForm>
                <SginUpBtn onClick={onSignup}>회원가입</SginUpBtn>
              </SginUpForm>
            </LoginForm>
          </LoginBox>
        )}
      </LoginSide>
    </LoginContainer>
    // <div className="signup-box">
    //   <h2>회원가입</h2>
    //   <div className="input-box">
    //     <label>User ID</label>
    //     <input
