import { Unity, useUnityContext } from "react-unity-webgl";
import { useState, useEffect } from "react";
import styled from "styled-components";

// 게임을 로드할 화면을 만듬
const Container = styled.div`
  width: 1344px;
  height: 756px;
  margin: auto;
  border: 1px solid gray;
`;

export function UnityProject() {
  const { unityProvider, sendMessage, addEventListener, removeEventListener } =
    useUnityContext({
      loaderUrl: "build/Build.loader.js",
      dataUrl: "build/Build.data",
      frameworkUrl: "build/Build.framework.js",
      codeUrl: "build/Build.wasm",
    });

  const [playingGame, setPlayingGame] = useState(true);
  const [isGameOver, setIsGameOver] = useState(false);
  const [userName, setUserName] = useState();
  const [score, setScore] = useState();
  const [userId, setUserId] = useState("");

  async function Axios() {
    try {
      const loginCurrent = await getCurrentUser();
    } catch (error) {
      console.log(error);
    }
  }

  // 상태를 로컬 스토리지에 저장
  const saveGameState = () => {
    sendMessage("GameController", "SaveGameState");
  };

  // 상태를 로컬 스토리지에서 불러오기
  const loadGameState = () => {
    const savedState = localStorage.getItem("unityGameState");
    if (savedState) {
      sendMessage("GameController", "LoadGameState", savedState);
    }
  };

  function handleGameOver(userName, score) {
    setIsGameOver(true);
    setUserName(userName);
    setScore(score);
    saveGameState();
  }

  // 컴포넌트가 마운트되었을 때 상태를 로드하고, 일정 간격으로 상태를 자동으로 저장
  useEffect(() => {
    addEventListener("GameOver", handleGameOver);

    // 페이지 로드 시 상태 복원
    loadGameState();

    // 일정 간격(예: 10초)으로 상태를 자동으로 저장
    const autoSaveInterval = setInterval(() => {
      saveGameState();
    }, 10000); // 10초마다 저장

    return () => {
      removeEventListener("GameOver", handleGameOver);
      clearInterval(autoSaveInterval); // 컴포넌트 언마운트 시 인터벌 클리어
    };
  }, []);

  return (
    <>
      <h1>UnityProject Game</h1>
      <Container>
        {playingGame ? (
          <Unity
            unityProvider={unityProvider}
            style={{
              width: "100%",
              height: "100%",
            }}
          />
        ) : null}
      </Container>

      {/* Unity에서 게임 오버 메시지를 받으면 출력 후 종료 */}
      {isGameOver === true && (
        <p>{`Game Over ${userName}! You've scored ${score} points.`}</p>
      )}
    </>
  );
}
