import { Unity, useUnityContext } from "react-unity-webgl";
import { useState, useEffect } from "react";
import styled from "styled-components";

// 게임을 로드할 화면을 만듬
const Container = styled.div`
  width: 1344px;
  height: 756px;
  margin: auto;
  border: 1px solid gray;
`;

export function UnityProject() {
  const [playingGame, setPlayingGame] = useState(true);
  const [isGameOver, setIsGameOver] = useState(false);
  const [userName, setUserName] = useState("");
  const [score, setScore] = useState(0);

  // React 에서 Unity 로 sendMessage 를 통해 전달하기
  const { unityProvider, sendMessage, addEventListener, removeEventListener } =
    useUnityContext({
      loaderUrl: "build/Build.loader.js",
      dataUrl: "build/Build.data",
      frameworkUrl: "build/Build.framework.js",
      codeUrl: "build/Build.wasm",
    });

  // 게임 오버 핸들러
  function handleGameOver(userName, score) {
    setIsGameOver(true);
    setUserName(userName);
    setScore(score);

    // 게임 데이터를 로컬 스토리지에 저장
    const gameData = { userName, score };
    localStorage.setItem("unityGameData", JSON.stringify(gameData));
  }

  // 컴포넌트가 마운트될 때 로컬 스토리지에서 게임 데이터 불러오기
  useEffect(() => {
    const savedGameData = localStorage.getItem("unityGameData");
    if (savedGameData) {
      const { userName, score } = JSON.parse(savedGameData);
      setUserName(userName);
      setScore(score);
      setIsGameOver(true); // 게임 오버 상태로 복구
    }

    // Unity 이벤트 리스너 추가
    addEventListener("GameOver", handleGameOver);
    return () => {
      removeEventListener("GameOver", handleGameOver);
    };
  }, [addEventListener, removeEventListener]);

  return (
    <>
      <h1>UnityProject Game</h1>
      <button onClick={() => setPlayingGame(true)}>Start Game</button>
      <button onClick={() => sendMessage("Player", "Attack")}>Attack</button>
      <Container>
        {playingGame ? (
          <Unity
            unityProvider={unityProvider}
            style={{
              width: "100%",
              height: "100%",
            }}
          />
        ) : null}
      </Container>

      {/* Unity에서 게임오버 메시지를 받으면 출력후 종료 */}
      {isGameOver === true && (
        <p>{`Game Over ${userName}! You've scored ${score} points.`}</p>
      )}
    </>
  );
}
