import { useEffect, useState } from "react";
import { getAllEvent } from "../../../Api/CommunityApi/CommunityApi";
import styled from "styled-components";

const SlideshowContainer = styled.div`
  position: relative;
  width: 100%;
  height: auto;
  overflow: hidden;
`;

const Slide = styled.div`
  display: flex;
  transition: transform 0.5s ease-in-out;
  transform: translateX(${(props) => props.index * -100}%);
`;

const EventImg = styled.img`
  width: 10%;
  height: auto;
`;

const PrevButton = styled.button`
  position: absolute;
  top: 50%;
  left: 10px;
  transform: translateY(-50%);
  z-index: 10;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
`;

const NextButton = styled.button`
  position: absolute;
  top: 50%;
  right: 10px;
  transform: translateY(-50%);
  z-index: 10;
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
`;

export function Home() {
  const [eventData, setEventData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    async function fetchEventData() {
      const eventAxios = await getAllEvent();
      setEventData(eventAxios);
    }
    fetchEventData();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === eventData.length - 1 ? 0 : prevIndex + 1
      );
    }, 5000); // 5초마다 슬라이드 변경

    return () => clearInterval(interval); // 컴포넌트 언마운트 시 인터벌 정리
  }, [eventData.length]);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === eventData.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? eventData.length - 1 : prevIndex - 1
    );
  };

  return (
    <SlideshowContainer>
      <Slide index={currentIndex}>
        {eventData.map((data, index) => (
          <EventImg key={index} src={data.imagePath} alt={`slide-${index}`} />
        ))}
      </Slide>
      <PrevButton onClick={prevSlide}>&#10094;</PrevButton>
      <NextButton onClick={nextSlide}>&#10095;</NextButton>
    </SlideshowContainer>
  );
}
