import { Unity, useUnityContext } from "react-unity-webgl";
import { useState, useEffect } from "react";
import styled from "styled-components";

// 게임을 로드할 화면을 만듬
const Container = styled.div`
  width: 1344px;
  height: 756px;
  margin: auto;
  border: 1px solid gray;
`;

export function UnityProject() {
  const [playingGame, setPlayingGame] = useState(true);
  const [isGameOver, setIsGameOver] = useState(false);
  const [userName, setUserName] = useState();

  const { unityProvider, sendMessage, addEventListener, removeEventListener } =
    useUnityContext({
      loaderUrl: "build/Build.loader.js",
      dataUrl: "build/Build.data",
      frameworkUrl: "build/Build.framework.js",
      codeUrl: "build/Build.wasm",
    });

  function handleGameOver(userName) {
    setIsGameOver(true);
    setUserName(userName);
    localStorage.setItem(
      "gameState",
      JSON.stringify({ isGameOver: true, userName })
    );
  }

  function loadGameState() {
    const savedState = localStorage.getItem("gameState");
    if (savedState) {
      const { isGameOver, userName } = JSON.parse(savedState);
      setIsGameOver(isGameOver);
      setUserName(userName);
    }
  }

  useEffect(() => {
    // 게임 상태를 로드
    loadGameState();

    // 유니티 이벤트 리스너 등록
    addEventListener("GameOver", handleGameOver);

    // 클린업 함수
    return () => {
      removeEventListener("GameOver", handleGameOver);
    };
  }, []);

  useEffect(() => {
    // 상태가 변경될 때마다 로컬 스토리지에 저장
    localStorage.setItem("gameState", JSON.stringify({ isGameOver, userName }));
  }, [isGameOver, userName]);

  return (
    <>
      <h1>UnityProject Game</h1>
      <button onClick={() => setPlayingGame(true)}>Start Game</button>
      <button onClick={() => sendMessage("Player", "Attack")}>Attack</button>
      <Container>
        {playingGame ? (
          <Unity
            unityProvider={unityProvider}
            style={{
              width: "100%",
              height: "100%",
            }}
          />
        ) : null}
      </Container>

      {isGameOver === true && <p>{`Game Over ${userName}!`}</p>}
    </>
  );
}
