import styled from "styled-components";
import { Navbar } from "../Navbar";
import { Sidebar } from "../Sidebar";
import { useEffect, useState } from "react";
import { getAllLecture } from "../../../Api/LectureApi/LectureApi";
import { IndexCommunity } from "../Community/IndexComunity";

const Container = styled.div`
  box-sizing: border-box;
  padding: 176px 32px 0 240px;
  transition: all 0.3s;
`;

export function Index() {
  const [x, setX] = useState(0);
  const [timer, setTimer] = useState(null);
  const [lecture, setLecture] = useState([]);
  const [images, setImages] = useState([]);

  useEffect(() => {
    axios();
  }, []);

  async function axios() {
    const lectureData = await getAllLecture();
    setLecture(lectureData);
    setImages(lectureData.slice(0, 8));
  }

  const updateGallery = (newX) => {
    setX(newX);
    if (timer) clearTimeout(timer);
    const newTimer = setTimeout(() => {
      setX((prevX) => prevX - 45);
    }, 6000);
    setTimer(newTimer);
  };

  useEffect(() => {
    updateGallery(x);
    // Clean up timer on component unmount
    return () => clearTimeout(timer);
  }, [x]);

  return (
    <>
      <Navbar />
      <Sidebar />
      <Container>
        <IndexCommunity />
      </Container>
    </>
  );
}
