import { useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
} from "../../../Api/CommunityApi/CommunityApi";

const BoardContainer = styled.div`
  width: 100%;
  height: 200px;
  display: grid;
  grid-template-columns: 50% 50%;
  padding: 10px;
`;

const Notices = styled.div`
  width: 100%;
  height: 50px;
  border: 1px solid orange;
`;
const Event = styled.div`
  width: 100%;
  height: 50px;
  border: 1px solid blue;
`;

export function IndexCommunity() {
  const [notices, setNoteces] = useState([]);
  const [events, setEvents] = useState([]);

  async function axios() {
    try {
      const noticesData = await getAllNotices();
      setNoteces(noticesData.slice(0, 5));

      const eventsData = await getAllLmsEvents();
      setEvents(eventsData.slice(0, 5));
    } catch (error) {
      console.log("Error", error);
    }
  }
  return (
    <BoardContainer>
      <Notices>
        <p></p>
      </Notices>
      <Event></Event>
    </BoardContainer>
  );
}
