import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
} from "../../../Api/CommunityApi/CommunityApi";
import { useNavigate } from "react-router-dom";

const BoardContainer = styled.div`
  width: 90%;
  display: grid;
  grid-template-columns: 40% 10% 40%;
  padding: 20px;
  margin: 0 auto;
  float: right;
`;

const Notices = styled.div`
  width: 100%;
  padding: 5px;
`;

const Event = styled.div`
  width: 100%;
  padding: 5px;
`;

const CommunityInfo = styled.p`
  width: 100%;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 800;
  margin-bottom: 5px;
  border-bottom: 1px solid #1a1b24;
  padding-bottom: 10px;
`;

const NoticesText = styled.p`
  color: #fff;
  font-size: 13px;
  margin-bottom: 8px;
  overflow: hidden;
  cursor: pointer;
`;

const More = styled.span`
  color: #fff;
  float: right;
  font-size: 30px;
  margin-top: -10px;
  padding: 0 10px;
  cursor: pointer;
`;

const ModalTitle = styled.p`
  font-size: 24px;
  font-weight: 700;
  background-color: #fff;
  border-bottom: 1px solid #1a1b24;
  padding: 20px;
  margin: 0;
`;

const ModalContent = styled.p`
  font-size: 18px;
  background-color: #fff;
  padding: 20px;
  margin: 0;
  line-height: 1.5;
`;

const ModalFooter = styled.div`
  display: flex;
  justify-content: space-between;
  border-top: 1px solid #1a1b24;
  padding-top: 15px;
`;

const Modal = ({ data, onClose, type }) => (
  <div style={modalStyles}>
    <div style={modalContentStyles}>
      <span style={closeButtonStyles} onClick={onClose}>
        &times;
      </span>
      <ModalTitle>{data.title}</ModalTitle>
      <ModalContent>{data.content}</ModalContent>
      <ModalFooter>
        {type === "notice" ? (
          <>
            <ModalContent>개시일 : {data.writingDate}</ModalContent>
            <ModalContent>조회수 : {data.viewCount}회</ModalContent>
            <ModalContent>작성자 : {data.userName}</ModalContent>
          </>
        ) : (
          <ModalContent>이벤트 날짜 : {data.eventDate}</ModalContent>
        )}
      </ModalFooter>
    </div>
  </div>
);

const modalStyles = {
  position: "fixed",
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: "rgba(0, 0, 0, 0.5)",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
};

const modalContentStyles = {
  backgroundColor: "#fff",
  padding: "20px",
  borderRadius: "8px",
  position: "relative",
  width: "80%",
  maxWidth: "800px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
};

const closeButtonStyles = {
  position: "absolute",
  top: "10px",
  right: "10px",
  fontSize: "24px",
  cursor: "pointer",
  backgroundColor: "#fff",
};

export function IndexCommunity() {
  const [notices, setNoteces] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedData, setSelectedData] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [modalType, setModalType] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    axios();
  }, []);

  async function axios() {
    const noticesData = await getAllNotices();
    setNoteces(noticesData.content);

    const eventsData = await getAllLmsEvents();
    setEvents(eventsData.content.slice(0, 5));
  }

  const openModal = (data, type) => {
    setSelectedData(data);
    setModalType(type);
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
    setSelectedData(null);
  };

  return (
    <BoardContainer>
      <Notices>
        <More
          onClick={() => {
            navigate("/community/notices");
          }}
        >
          &#43;
        </More>
        <CommunityInfo>공지사항</CommunityInfo>
        {notices.map((data, index) => (
          <NoticesText key={index} onClick={() => openModal(data, "notice")}>
            {data.lmsNoticesTitle}
          </NoticesText>
        ))}
      </Notices>
      <div></div>
      <Event>
        <More>&#43;</More>
        <CommunityInfo>이벤트</CommunityInfo>
        {events.map((data, index) => (
          <NoticesText key={index} onClick={() => openModal(data, "event")}>
            {data.lmsEventsTitle}
          </NoticesText>
        ))}
      </Event>
      {modalOpen && selectedData && (
        <Modal data={selectedData} onClose={closeModal} type={modalType} />
      )}
    </BoardContainer>
  );
}
