import React, { useState, useEffect } from "react";

export function Home() {
  // 슬라이드에 표시할 이미지 URL 배열을 정의합니다.
  const slides = [
    "https://via.placeholder.com/600x300/ff7f7f/333333?text=Slide+1",
    "https://via.placeholder.com/600x300/7fff7f/333333?text=Slide+2",
    "https://via.placeholder.com/600x300/7f7fff/333333?text=Slide+3",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) =>
        prevIndex === slides.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000); // 3초마다 슬라이드 변경

    return () => clearInterval(interval); // 컴포넌트 언마운트 시 인터벌 정리
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === slides.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? slides.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slideshow">
      <div className="slide" key={currentIndex}>
        <img src={slides[currentIndex]} alt={`slide ${currentIndex}`} />
      </div>
      <button className="prev" onClick={prevSlide}>
        &#10094;
      </button>
      <button className="next" onClick={nextSlide}>
        &#10095;
      </button>
    </div>
  );
}
