import { useParams } from "react-router-dom";
import { getContentsByLectureId } from "../../../Api/LeaningApi/LeaningApi";
import { useEffect, useState } from "react";

export function Course() {
  const { userId, lectureId } = useParams();
  const [learning, setlearing] = useState([]);
  const [lecture, setLecture] = useState([]);

  useEffect(() => {
    learningContents();
  }, [lectureId]);

  async function learningContents() {
    const learningAxiox = await getContentsByLectureId(lectureId);
    console.log(learningAxiox[0]);
    setlearing(learningAxiox);

    // setLecture(learningAxiox[0]);
    // console.log(lecture);
  }
  const lectureData = learningAxiox[0];
  console.log(lectureData);
  return (
    <>
      <p>　</p>
      <p>*학습목차*</p>
      {learning.map((data, index) => (
        <p key={index}>
          {index}.{data.learningContents}
        </p>
      ))}
      <p>　</p>
      <p>*Lecture Data*</p>
    </>
  );
}
