import styled from "styled-components";
import { Navbar } from "../Navbar";
import { Sidebar } from "../Sidebar";
import { useEffect, useState } from "react";

const Container = styled.div`
  box-sizing: border-box;
  padding: 176px 32px 0 240px;
  transition: all 0.3s;
`;

const ImageContainer = styled.div`
  position: relative;
  width: 140px;
  height: 140px;
  transform-style: preserve-3d;
  transform: perspective(1000px) rotateY(${(props) => props.rotation}deg);
  transition: transform 0.7s;
`;

const Span = styled.span`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  transform: rotateY(calc(var(--i) * 45deg)) translateZ(400px);
`;

const Img = styled.img`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
`;

const BtnContainer = styled.div`
  margin-top: 20px;
`;

const Btn = styled.button`
  margin: 0 10px;
`;

export function Index() {
  const [x, setX] = useState(0);
  const [timer, setTimer] = useState(null);

  const updateGallery = (newX) => {
    setX(newX);
    if (timer) clearTimeout(timer);
    const newTimer = setTimeout(() => {
      setX((prevX) => prevX - 45);
    }, 3000);
    setTimer(newTimer);
  };

  useEffect(() => {
    updateGallery(x);
    // Clean up timer on component unmount
    return () => clearTimeout(timer);
  }, [x]);

  return (
    <>
      <Navbar />
      <Sidebar />
      <Container>
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            height: "100vh",
            margin: 0,
            paddingtop: "100px",
          }}
        >
          <ImageContainer rotation={x}>
            {[...Array(8)].map((_, i) => (
              <Span key={i} style={{ "--i": i + 1 }}>
                <Img src={`${i + 1}.jpg`} alt={`Image ${i + 1}`} />
              </Span>
            ))}
          </ImageContainer>
          <BtnContainer>
            <Btn onClick={() => updateGallery(x + 45)}>Prev</Btn>
            <Btn onClick={() => updateGallery(x - 45)}>Next</Btn>
          </BtnContainer>
        </div>
      </Container>
    </>
  );
}
