import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getLecture } from "../../../Api/LectureApi/LectureApi";
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: 20% 80%;
`;

const SideMenu = styled.div`
  width: 100%;
  border: 1px solid black;
`;

const Content = styled.div`
  width: 100%;
  border: 1px solid yellow;
`;

const LectureImg = styled.img`
  width: 50%;
`;

export function LectureDetail() {
  const { lectureId } = useParams(); // URL에서 lectureId를 가져옴
  const [lecture, setLecture] = useState(null); // lecture 데이터를 저장할 상태
  const [loading, setLoading] = useState(true); // 로딩 상태 관리
  const [error, setError] = useState(null); // 오류 상태 관리

  useEffect(() => {
    async function fetchLecture() {
      try {
        const data = await getLecture(lectureId); // getLecture API 호출
        setLecture(data); // 가져온 데이터를 상태로 저장
      } catch (err) {
        setError(err); // 오류가 발생하면 오류 상태로 설정
      } finally {
        setLoading(false); // 로딩 상태를 종료
      }
    }

    fetchLecture(); // 컴포넌트가 마운트될 때 API 호출
  }, [lectureId]); // lectureId가 변경될 때마다 다시 호출

  if (loading) {
    return <p>Loading...</p>; // 로딩 중일 때 표시할 내용
  }

  if (error) {
    return <p>Error: {error.message}</p>; // 오류 발생 시 표시할 내용
  }

  if (!lecture) {
    return <p>No data found.</p>; // 데이터가 없을 때 표시할 내용
  }
  return (
    <Container>
      <SideMenu></SideMenu>
      <Content>
        <LectureImg src={lecture.imagePath}></LectureImg>
        <p>Lecture ID: {lectureId}</p>
        <p>Lecture Title: {lecture.lectureName}</p>
        <p>Lecture Description: {lecture.learningObject}</p>
      </Content>
    </Container>
  );
}
