import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { LectureList } from "./Components/LMS/Lecture/LectureList";
import { Cart } from "./Components/LMS/Cart/Cart";
import { Lecture } from "./Components/LMS/Lecture/Lecture";
import { LMS } from "./Components/LMS/LMS";

// Styled components 정의
const Wrap = styled.div`
  position: relative;
  top: ${(props) => props.top}vh;
  width: 100%;
  height: 100%;
  transition: top 0.5s;
`;

const Container = styled.div`
  width: 100%;
  height: 100vh;
  background-color: ${(props) => props.bgColor};
`;

function App() {
  const [page, setPage] = useState(0);
  const lastPage = 3; // 페이지 수 - 1

  useEffect(() => {
    const handleScroll = (e) => {
      e.preventDefault();
      if (e.deltaY > 0) {
        setPage((prevPage) => Math.min(prevPage + 1, lastPage));
      } else if (e.deltaY < 0) {
        setPage((prevPage) => Math.max(prevPage - 1, 0));
      }
    };

    window.addEventListener("wheel", handleScroll, { passive: false });

    return () => {
      window.removeEventListener("wheel", handleScroll);
    };
  }, [lastPage]);

  return (
    <Wrap top={page * -100}>
      {/* 1페이지 */}
      <Container bgColor="#9e97cb">
        <LMS></LMS>
      </Container>

      {/* 2페이지 */}
      <Container bgColor="#dbd8eb"></Container>

      {/* 3페이지 */}
      <Container bgColor="#348edd"></Container>

      {/* 4페이지 */}
      <Container bgColor="#d4daea"></Container>
    </Wrap>
  );
}

export default App;
