import { useEffect, useState } from "react";
import { getCurrentUser } from "../../../Api/UserApi/UserApi";

export function Cart() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    cartList();
  });

  // 장바구니 리스트
  async function cartList() {
    let userId;
    let cartList;
    try {
      const SessionData = await getCurrentUser();
      userId = SessionData.userId;
      cartList = JSON.parse(localStorage.getItem(userId));
      setCart(cartList);
    } catch (error) {
      console.log("Cart List Error");
    }
  }

  async function cartRemove() {
    let userId;
    let cartList;

    try {
      const SessionData = await getCurrentUser();
      userId = SessionData.userId;
      cartList = JSON.parse(localStorage.getItem(userId));

      if (cartList) {
        cartList = cartList.filter((item) => item.lectureId);
        localStorage.setItem(userId, JSON.stringify(cartList));
      }
    } catch (error) {
      console.log("Session Error", error);
    }
  }

  return (
    <>
      <p>　</p>
      <p>*CartList*</p>
      <div>
        {cart.length > 0 ? (
          cart.map((lecture, index) => (
            <div>
              <input type="checkbox" key={index}></input>
              <p key={index}>Lecture ID: {lecture.lectureId}</p>
            </div>
          ))
        ) : (
          <p>Your cart is empty.</p>
        )}
      </div>

      <p>　</p>
      <p>*CartRemove*</p>
      <button onClick={cartRemove}>삭제</button>
    </>
  );
}
