import React, { useState, useEffect } from "react";
import styled from "styled-components";

const SlideshowContainer = styled.div`
  position: relative;
  max-width: 600px;
  margin: auto;
  overflow: hidden;
`;

const Slide = styled.div`
  display: flex;
  transition: transform 0.5s ease-in-out;
  transform: translateX(${(props) => props.translateValue}%);
`;

const SlideImage = styled.img`
  width: 100%;
  flex-shrink: 0;
`;

const PrevButton = styled.button`
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
  z-index: 1;

  &:hover {
    background-color: rgba(0, 0, 0, 0.8);
  }
`;

const NextButton = styled.button`
  position: absolute;
  top: 50%;
  right: 0;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  color: white;
  border: none;
  padding: 10px;
  cursor: pointer;
  z-index: 1;

  &:hover {
    background-color: rgba(0, 0, 0, 0.8);
  }
`;

export function Home() {
  const slides = [
    "https://via.placeholder.com/600x300/ff7f7f/333333?text=Slide+1",
    "https://via.placeholder.com/600x300/7fff7f/333333?text=Slide+2",
    "https://via.placeholder.com/600x300/7f7fff/333333?text=Slide+3",
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      nextSlide();
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === slides.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? slides.length - 1 : prevIndex - 1
    );
  };

  const translateValue = -(currentIndex * 100);

  return (
    <SlideshowContainer>
      <Slide translateValue={translateValue}>
        {slides.map((slide, index) => (
          <SlideImage src={slide} alt={`Slide ${index}`} key={index} />
        ))}
      </Slide>
      <PrevButton onClick={prevSlide}>&#10094;</PrevButton>
      <NextButton onClick={nextSlide}>&#10095;</NextButton>
    </SlideshowContainer>
  );
}
