import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
} from "../../../Api/CommunityApi/CommunityApi";
import { useNavigate } from "react-router-dom";

const BoardContainer = styled.div`
  width: 90%;
  display: grid;
  grid-template-columns: 40% 10% 40%;
  padding: 20px;
  margin: 0 auto;
  float: right;
`;

const Notices = styled.div`
  width: 100%;
  padding: 5px;
`;
const Event = styled.div`
  width: 100%;
  padding: 5px;
`;

const CommunityInfo = styled.p`
  width: 100%;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 800;
  margin-bottom: 5px;
  border-bottom: 1px solid #1a1b24;
  padding-bottom: 10px;
`;

const NoticesText = styled.p`
  color: #fff;
  font-size: 13px;
  margin-bottom: 8px;
  overflow: hidden;
  cursor: pointer;
`;

const More = styled.span`
  color: #fff;
  float: right;
  font-size: 30px;
  margin-top: -10px;
  padding: 0 10px;
  cursor: pointer;
`;
const ModalTitle = styled.p`
  font-size: 24px;
  font-weight: 700;
  background-color: #f8f9fa;
  border-bottom: 2px solid #343a40;
  padding: 20px;
  margin: 0;
`;

const ModalContent = styled.p`
  font-size: 16px;
  background-color: #fff;
  padding: 15px;
  margin: 0;
  line-height: 1.5;
`;

const ModalFooter = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 20px;
  border-top: 1px solid #dee2e6;
  padding-top: 15px;
`;

const Modal = ({ notice, onClose }) => (
  <div style={modalStyles}>
    <div style={modalContentStyles}>
      <span style={closeButtonStyles} onClick={onClose}>
        &times;
      </span>
      <ModalTitle>{notice.lmsNoticesTitle}</ModalTitle>
      <ModalContent>{notice.lmsNoticesContent}</ModalContent>
      <ModalFooter>
        <ModalContent>조회수 : {notice.lmsNoticesViewCount}회</ModalContent>
        <ModalContent>개시일 : {notice.lmsNoticesWritingDate}</ModalContent>
        <ModalContent>작성자 : {notice.user.userNameKor}</ModalContent>
      </ModalFooter>
    </div>
  </div>
);

const modalStyles = {
  position: "fixed",
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: "rgba(0, 0, 0, 0.5)",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  zIndex: 1000,
};

const modalContentStyles = {
  backgroundColor: "#fff",
  padding: "20px",
  borderRadius: "8px",
  position: "relative",
  width: "80%",
  maxWidth: "800px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
  overflow: "auto",
};

