    setEvents(eventsData.content.slice(0, 5));
  }

  const openModal = (notice) => {
    setSelectedNotice(notice);
    setNoticeModal(true);
  };

  const closeModal = () => {
    setNoticeModal(false);
    setSelectedNotice(null);
  };
  console.log(notices);

  return (
    <BoardContainer>
      <Notices>
        <More
          onClick={() => {
            navigate("/community/notices");
          }}
        >
          &#43;
        </More>
        <CommunityInfo>공지사항</CommunityInfo>
        {notices.map((data, index) => (
          <NoticesText key={index} onClick={() => openModal(data)}>
            {data.lmsNoticesTitle}
          </NoticesText>
        ))}
      </Notices>
      <div></div>
      <Event>
        <More>&#43;</More>
        <CommunityInfo>이벤트</CommunityInfo>
        {events.map((data, index) => (
          <NoticesText key={index}>{data.lmsEventsTitle}</NoticesText>
        ))}
      </Event>
      {noticeModal && selectedNotice && (
        <Modal notice={selectedNotice} onClose={closeModal} />
      )}
    </BoardContainer>
  );
}
