import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
} from "../../../Api/CommunityApi/CommunityApi";
import { useNavigate } from "react-router-dom";

const BoardContainer = styled.div`
  width: 90%;
  display: grid;
  grid-template-columns: 40% 10% 40%;
  padding: 20px;
  margin: 0 auto;
  float: right;
`;

const Notices = styled.div`
  width: 100%;
  padding: 5px;
`;
const Event = styled.div`
  width: 100%;
  padding: 5px;
`;

const CommunityInfo = styled.p`
  width: 100%;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 800;
  margin-bottom: 5px;
  border-bottom: 1px solid #1a1b24;
  padding-bottom: 10px;
`;

const NoticesText = styled.p`
  color: #fff;
  font-size: 13px;
  margin-bottom: 8px;
  overflow: hidden;
  cursor: pointer;
`;

const More = styled.span`
  color: #fff;
  float: right;
  font-size: 30px;
  margin-top: -10px;
  padding: 0 10px;
  cursor: pointer;
`;

const ModalTitel = styled.p`
  font-size: 24px;
  font-weight: 700;
  background-color: #fff;
  border-bottom: 1px solid #1a1b24;
  padding: 10px;
`;

const ModalContent = styled.p`
  font-size: 18px;
  background-color: #fff;
  padding: 10px;
`;

const Modal = ({ notice, onClose }) => (
  <div style={modalStyles}>
    <div style={modalContentStyles}>
      <span style={closeButtonStyles} onClick={onClose}>
        &times;
      </span>
      <ModalTitel>{notice.lmsNoticesTitle}</ModalTitel>
      <ModalContent>{notice.lmsNoticesContent}</ModalContent>
      <ModalContent>{notice.lmsNoticesViewCount}회</ModalContent>
      <ModalContent>{notice.lmsNoticesWritingDate}</ModalContent>
    </div>
  </div>
);

const modalStyles = {
  position: "fixed",
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  backgroundColor: "rgba(0, 0, 0, 0.5)",
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
};

const modalContentStyles = {
  backgroundColor: "#fff",
  padding: "20px",
  borderRadius: "5px",
  position: "relative",
  width: "80%",
  maxWidth: "900px",
};

const closeButtonStyles = {
  position: "absolute",
  top: "10px",
  right: "10px",
  fontSize: "24px",
  cursor: "pointer",
  backgroundColor: "#fff",
};

export function IndexCommunity() {
  const [notices, setNoteces] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedNotice, setSelectedNotice] = useState(null);
  const [noticeModal, setNoticeModal] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    axios();
  }, []);

  async function axios() {
    const noticesData = await getAllNotices();
    setNoteces(noticesData.content);

    const eventsData = await getAllLmsEvents();
    setEvents(eventsData.content.slice(0, 5));
  }

  const openModal = (notice) => {
    setSelectedNotice(notice);
    setNoticeModal(true);
  };

  const closeModal = () => {
    setNoticeModal(false);
    setSelectedNotice(null);
  };
  console.log(notices);

  return (
    <BoardContainer>
      <Notices>
        <More
          onClick={() => {
            navigate("/community/notices");
          }}
        >
          &#43;
        </More>
        <CommunityInfo>공지사항</CommunityInfo>
        {notices.map((data, index) => (
          <NoticesText key={index} onClick={() => openModal(data)}>
            {data.lmsNoticesTitle}
          </NoticesText>
        ))}
      </Notices>
      <div></div>
      <Event>
        <More>&#43;</More>
        <CommunityInfo>이벤트</CommunityInfo>
        {events.map((data, index) => (
          <NoticesText key={index}>{data.lmsEventsTitle}</NoticesText>
        ))}
      </Event>
      {noticeModal && selectedNotice && (
        <Modal notice={selectedNotice} onClose={closeModal} />
      )}
    </BoardContainer>
  );
}
