import { Unity, useUnityContext } from "react-unity-webgl";
import { useState, useEffect } from "react";
import styled from "styled-components";

const Container = styled.div`
  width: 1344px;
  height: 756px;
  margin: auto;
  border: 1px solid gray;
`;

export function UnityProject() {
  const [playingGame, setPlayingGame] = useState(true);
  const [isGameOver, setIsGameOver] = useState(false);
  const [userName, setUserName] = useState();
  const [score, setScore] = useState();

  const { unityProvider, sendMessage, addEventListener, removeEventListener } =
    useUnityContext({
      loaderUrl: "build/Build.loader.js",
      dataUrl: "build/Build.data",
      frameworkUrl: "build/Build.framework.js",
      codeUrl: "build/Build.wasm",
    });

  function handleGameOver(userName, score) {
    setIsGameOver(true);
    setUserName(userName);
    setScore(score);
  }

  useEffect(() => {
    addEventListener("GameOver", handleGameOver);
    return () => {
      removeEventListener("GameOver", handleGameOver);
    };
  }, []);

  useEffect(() => {
    // 저장된 씬 상태 복원하기
    const savedScene = localStorage.getItem("currentScene");
    if (savedScene) {
      sendMessage("SceneManager", "LoadScene", savedScene);
    }
  }, [sendMessage]);

  useEffect(() => {
    // 씬 상태를 로컬 스토리지에 저장하기 위한 메시지 처리
    const handleSceneChange = (sceneName) => {
      localStorage.setItem("currentScene", sceneName);
      console.log(sceneName);
    };

    // Unity에서 씬 상태 변경 이벤트를 받으면 로컬 스토리지에 저장
    window.addEventListener("sceneChanged", (event) => {
      handleSceneChange(event.detail.sceneName);
    });

    return () => {
      window.removeEventListener("sceneChanged", handleSceneChange);
    };
  }, []);

  return (
    <>
      <h1>UnityProject Game</h1>
      <button onClick={() => setPlayingGame(true)}>Start Game</button>
      <button onClick={() => sendMessage("Player", "Attack")}>Attack</button>
      <Container>
        {playingGame ? (
          <Unity
            unityProvider={unityProvider}
            style={{
              width: "100%",
              height: "100%",
            }}
          />
        ) : null}
      </Container>

      {isGameOver && (
        <p>{`Game Over ${userName}! You've scored ${score} points.`}</p>
      )}
    </>
  );
}
