import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
} from "../../../Api/CommunityApi/CommunityApi";

const BoardContainer = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: 50% 50%;
  padding: 10px;
`;

const Notices = styled.div`
  width: 100%;
  padding: 5px;
`;
const Event = styled.div`
  width: 100%;
  padding: 5px;
`;
const NoticesText = styled.p`
  color: #fff;
  font-size: 13px;
  margin-bottom: 5px;
  overflow: hidden;
`;

const CommunityInfo = styled.p`
  width: 100%;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 800;
  margin-bottom: 5px;
  border-bottom: 1px solid #1a1b24;
`;
export function IndexCommunity() {
  const [notices, setNoteces] = useState([]);
  const [events, setEvents] = useState([]);

  useEffect(() => {
    axios();
  }, []);

  async function axios() {
    try {
      const noticesData = await getAllNotices();
      setNoteces(noticesData.content);

      const eventsData = await getAllLmsEvents();
      setEvents(eventsData.content.slice(0, 5));
    } catch (error) {
      console.log("Error", error);
    }
  }

  console.log("1", notices);
  console.log("2", events);
  return (
    <BoardContainer>
      <Notices>
        <CommunityInfo>공지사항</CommunityInfo>
        {notices.map((data, index) => (
          <NoticesText key={index}>{data.lmsNoticesTitle}</NoticesText>
        ))}
      </Notices>
      <Event>
        <CommunityInfo>이벤트</CommunityInfo>
        {events.map((data, index) => (
          <NoticesText key={index}>{data.lmsEventsTitle}</NoticesText>
        ))}
      </Event>
    </BoardContainer>
  );
}
