import { useEffect, useState } from "react";
import styled from "styled-components";
import {
  getAllLmsEvents,
  getAllNotices,
  getNoticeById,
} from "../../../Api/CommunityApi/CommunityApi";
import { useNavigate } from "react-router-dom";

const BoardContainer = styled.div`
  width: 100%;
  display: grid;
  grid-template-columns: 50% 50%;
  padding: 10px;
`;

const Notices = styled.div`
  width: 100%;
  padding: 5px;
`;
const Event = styled.div`
  width: 100%;
  padding: 5px;
`;

const CommunityInfo = styled.p`
  width: 100%;
  color: #fff;
  font-size: 1.5rem;
  font-weight: 800;
  margin-bottom: 5px;
  border-bottom: 1px solid #1a1b24;
`;

const NoticesText = styled.p`
  color: #fff;
  font-size: 13px;
  margin-bottom: 8px;
  overflow: hidden;
  cursor: pointer;
`;

const More = styled.span`
  color: #fff;
  float: right;
  font-size: 30px;
  margin-top: -10px;
  padding: 0 10px;
  cursor: pointer;
`;

const Modal = styled.div`
  position: fixed;
  z-index: 1000;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

export function IndexCommunity() {
  const [notices, setNoteces] = useState([]);
  const [events, setEvents] = useState([]);
  const [noticeModal, setNoticeModal] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    axios();
  }, []);

  async function axios() {
    const noticesData = await getAllNotices();
    setNoteces(noticesData.content);

    const eventsData = await getAllLmsEvents();
    setEvents(eventsData.content.slice(0, 5));
  }

  return (
    <BoardContainer>
      <Notices>
        <More
          onClick={() => {
            navigate("/community/notices");
          }}
        >
          &#43;
        </More>
        <CommunityInfo>공지사항</CommunityInfo>
        {notices.map((data, index) => (
          <NoticesText
            key={index}
            onClick={() => {
              navigate(`/community/notices/${data.lmsNoticesSeq}`);
            }}
          >
            {data.lmsNoticesTitle}
          </NoticesText>
        ))}
      </Notices>
      <Event>
        <More>&#43;</More>
        <CommunityInfo>이벤트</CommunityInfo>
        {events.map((data, index) => (
          <NoticesText key={index}>{data.lmsEventsTitle}</NoticesText>
        ))}
      </Event>
    </BoardContainer>
  );
}
