import React, { useState } from "react";
import axios from "axios";
import "../../../Styles/login.css";
import styled, { keyframes } from "styled-components";
// import { AuthProvider } from '../AuthContext';

const LoginContainer = styled.div`
  box-sizing: border-box;
  padding-right: 600px;
  overflow: hidden;
`;

const Loginbg = styled.div`
  width: 100%;
  height: 100vh;
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  animation-name: bg;
  animation-duration: 10s;
  animation-duration: leaner;
  animation-iteration-count: 1;
  animation-direction: alternate;
  animation-fill-mode: forwards;
`;

const LoginImg = styled.img`
  width: 100%;
  height: 100vh;
  background-repeat: no-repeat;
  background-position: center;
  background-size: cover;
  animation-name: bg;
  animation-duration: 10s;
  animation-timing-function: linear; /* 여기를 수정 */
  animation-iteration-count: 1;
  animation-direction: alternate;
  animation-fill-mode: forwards;
`;

const LoginForm = styled.div`
  width: 100%;
  padding: 40px;
`;

const InputBox = styled.input`
  display: block;
  margin-bottom: 10px;
  width: 400px;
  background-color: #1a1b24;
  -webkit-box-sizing: border-box;
  box-sizing: border-box;
  font-size: 16px;
  /* border: 1px solid #ECECEC; */
  border-radius: 12px;
  height: 72px;
  padding: 20px 0 0 23px;
  position: relative;
  z-index: 2;
  color: #fff;
`;

const SummitBtn = styled.button``;

export function Login({ onSignup }) {
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const data = { userId, password };
      const response = await axios.post(
        "http://localhost:8080/user/login",
        data,
        { withCredentials: true }
      );
      if (response.status === 200) {
        window.location.href = "home";
      }
    } catch (error) {
      alert("탈퇴 회원이거나, 잘못된 로그인 정보입니다.");
    }
  };

  return (
    <LoginContainer>
      <LoginImg></LoginImg>
      <LoginForm>
        <InputBox
          type="text"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          placeholder="아이디"
        />
        <InputBox
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="비밀번호"
        />
        <SummitBtn onClick={handleLogin} />
        {/* <SummitBtn onClick={onSignup} /> */}
      </LoginForm>
    </LoginContainer>

    // <div className="login-box">
    //   <h2>로그인</h2>
    //   <div className="input-box">
    //     <input
    //       type="text"
    //       value={userId}
    //       onChange={(e) => setUserId(e.target.value)}
    //       placeholder="아이디"
    //     />
    //     {/* <label>아이디</label> */}
    //   </div>
    //   <div className="input-box">
    //     <input
    //       type="password"
    //       value={password}
    //       onChange={(e) => setPassword(e.target.value)}
    //       placeholder="비밀번호"
    //     />
    //     {/* <label>비밀번호</label> */}
    //   </div>
    //   <div className="button-box">
    //     <div className="loginBtn" onClick={handleLogin}>
    //       로그인
    //     </div>
    //     <div className="signupBtn" onClick={onSignup}>
    //       회원가입
    //     </div>
    //   </div>
    // </div>
  );
}

export default Login;
